use std::vec::Vec;
use std::char::to_digit;
use std::cmp::max;


fn main() {
    let a = "";
    let b = a.chars().fold(Vec::new(), |v, c| v.push(to_digit(c, 10).unwrap() as int));
    b.iter().take(b.len() - 13).zip(b.iter().skip(13)).fold((0, 0), |z, (x, y)| {let (mut a, mut m) = z; a = a / (*x) * (*y); m = max(m ,a); (a, m)});
}
